clc;cla;clear all;capital = 100000;taux = 0.03;nbAnnee = 1;nbJour = nbAnnee / 365;resultat(1) = capital;resultat6Mois(1) = capital;resultatFrequence(1) = capital; # ---- point n°1 -------------------------------------------------------------------function resultat = calculInteret(capital, taux, nbAnnee)  resultat = capital * (1+taux)^nbAnnee;    returnendfunctionfor i=1 : nbAnnee  resultat(i+1) = calculInteret(capital, taux, i);endfor# ---- point n°2 -------------------------------------------------------------------function resultat = calculInteret6Mois(capital, taux, nbAnnee)  resultat = capital * (1+taux/2)^(nbAnnee*2);    returnendfunctionfor i=1 : nbAnnee  resultat6Mois(i+1) = calculInteret6Mois(capital, taux, i);endfor%hold on;%%plot(resultat);%%plot(resultat6Mois);%%hold off;# différence sur le nombre d'annéeresultat6Mois(nbAnnee) - resultat(nbAnnee)# ---- point n°3 ------------------------------------------------------------------- function resultat = calculInteretSelonF(capital, taux, nbAnnee, nbRetrait)  resultat = capital * (1+(taux/nbRetrait))^(nbAnnee*nbRetrait);    returnendfunctionhold on# Afficher plutôt tableau différentiel for i=1 : 365  for j=1 : nbAnnee    resultatFrequence(j+1) = calculInteretSelonF(capital, taux, j, i);  endfor  plot(resultatFrequence);  resultatFrequence = 0;endforhold off